import Formbuild from "./formbuild";
import DataCreator from "./dataCreator";
import { useState } from "react";

function App() {
  const [showHTML , setShowHTML] = useState(false)
  return (
    <div className="App">
      <DataCreator/>
      <button onClick={() => setShowHTML(value => !value)}>{showHTML ? "Hide" : "Show"} HTML</button>
      {showHTML && <Formbuild /> }  
    </div>
  );
}

export default App;
