import React from 'react'
import { useState , useRef } from 'react'
import "./dataCreator.css"



function DataCreator() {
    const [questionList , setQuestionList] = useState([])
    const [serialNo ,setSerialNo] = useState("")
    const [question , setQuestion] = useState("")
    const [num , setNum] = useState(1)

  return (
    <div>
      <input placeholder='enter serial no.' value={serialNo} onChange={(e)=> setSerialNo(e.target.value
      )}/>
      <input className='w-100' placeholder='enter question' value={question} onChange={(e)=> setQuestion(e.target.value)} />
      <button onClick={()=>{
        setNum(prev => prev+1)
        setQuestionList([...questionList , {
            serialNo , question ,num
          }])
          setSerialNo("")
          setQuestion("")
      } }>Add question</button>

    </div>
  )
}

export default DataCreator
