import React from "react";
let array = [
  {
    num: "1",
    question: "Is the SRPS licensed/certified for the operation of Recruitment and Placement services (Stating the SRPS meets the requirements of National laws and Regulations)",
    
  },
  {
    num: "2",
    question: "Himan is home",
    
  },
];

function keyCreator(keyquestion) {
  const singleQuestionKeyCombinationArray = [];
  const wordsArray = keyquestion.split(" ");
  for (let i = 0; i < wordsArray.length; i++) {
    for (let j = i + 1; j < wordsArray.length; j++) {
      const firstWord = wordsArray[i];
      const secondWord = wordsArray[j];
      const thirdWord = `${firstWord}_${secondWord}`;
      singleQuestionKeyCombinationArray.push(thirdWord);
    }
  }
  return singleQuestionKeyCombinationArray[
    Math.floor(Math.random() * singleQuestionKeyCombinationArray.length)
  ];
}

function Formbuild() {
  function StructureCreator({ num, question }) {
    const structure = `
    <div class="row-container page-break">
    <div class="row-fix">
        <div class="column w-5 align-center">
            <span class="font-style"> ${num} </span>
        </div>
        <div class="column w-45 align-left">
            <p>
                Is the SRPS licensed/certified for the operation of Recruitment and Placement
                services (Stating the SRPS meets the requirements of National laws and Regulations)
            </p>
        </div>
        <div class="column w-2-5 align-center">
            <span class="value_text">
                ::operation_of_recruitment_yes
            </span>
        </div>
        <div class="column w-2-5 align-center">
            <span class="value_text">
                ::operation_of_recruitment_no
            </span>
        </div>
        <div class="column w-7 align-center">
            <span class="font-style">
                A 1.4.6
            </span>
        </div>
        <div class="column w-38 align-left">
            <span class="font-style">
                ::operation_of_recruitment_remarks
            </span>
        </div>
    </div>
</div>
        `;

    return (
      <>
        {structure} <br />
      </>
    );
  }

  return (
    <div>
      {array.map((item) => (
        <>
          <StructureCreator
            num={item.num}
            question={item.question}
          />
        </>
      ))}
    </div>
  );
}

export default Formbuild;
