import React from 'react'
import { useState } from 'react'

function DataCreator() {
    const [questionList , setQuestionList] = useState([])
    const [serialNo ,setSerialNo] = useState("")
    const [question , setQuestion] = useState("")

    console.log(questionList,"s")   
  return (
    <div>
      <input placeholder='enter serial no.' onChange={(e)=> setSerialNo(e.target.value
      )}/>
      <input placeholder='enter question' onChange={(value)=> setQuestion(e.target.value)} />
      <button onClick={()=>{
        [...questionList , {
            serialNo , question
          }]
          setSerialNo("")
          setQuestion("")
      } }>Add question</button>
    </div>
  )
}

export default DataCreator
