import React from "react";
import { useState, useRef } from "react";
import "./dataCreator.css";
import questionsArray from "./data";
console.log(questionsArray,"qnsjqnj")
function DataCreator({setQuestionsArray}) {
  const [questionList ,setQuestionList] = useState([])
  const [serialNo, setSerialNo] = useState("");
  const [question, setQuestion] = useState("");
  const [num, setNum] = useState(1);
  function creatingNewData(){
    setQuestionsArray((prev) =>  [...questionList] )
  }
  return (
    <div>
      <input
        className="border-one p-3 mr-10"
        placeholder="enter serial no."
        value={serialNo}
        onChange={(e) => setSerialNo(e.target.value)}
      />
      <input
        className="w-100 border-one p-3 mr-10"
        placeholder="enter question"
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
      />
      <button
        className="p-10 border-none"
        onClick={() => {
          setNum((prev) => prev + 1);
          setQuestionList([
            ...questionList,
            {
              serialNo : `"${serialNo}"`,
              question : `"${question}"`,
              num : `"${num}"`
            },
          ]);
          setSerialNo("");
          setQuestion("");
        }}
      >
        Add question
      </button>
      <button onClick={()=> {
        creatingNewData()
      }}>Adding data to Database</button>
      <br />
      <div className="mt-15">
     DATA STRUCTURE IN OBJECT FORMAT
      </div>
      <div className="border-grey p-30 mb-20">
      
      {questionList.map((item) => {
        return (
          <>
          
          {` {
            serialNo : ${item.serialNo} , 
            question :${item.question},
            num :${item.num}
        },`}
            <br />
          
          </>
        );
      })}
      </div>
    </div>
  );
}

export default DataCreator;
