import "./App.css";
import Formbuild from "./formbuild";
import DataCreator from "./dataCreator";
import { useState } from "react";
function App() {
  const [showHTML , setShowHTMl] = useState(false);
  const [questionList , setQuestionList] = useState([])
  return (
    <div className="App">
      <DataCreator questionList={questionList} setQuestionList={setQuestionList}/>
      <button onClick={()=>setShowHTMl(value => !value)}>{`${showHTML === false ? "hide" : "Show"}`} html structure</button>
      {showHTML && <Formbuild questionList={questionList}/> }
    </div>
  );
}

export default App;
